import React, { Component } from 'react'
import api from '../../services/api'
import Routes from './routes'

export default class BotInfo extends Component{
	
	constructor(props) {
	    super(props);
	    this.state = { bot: {},
		 	newName: '',
		 	redirect: false};

	    this.handleChange = this.handleChange.bind(this);
	    this.send = this.send.bind(this);
  	}



	

	async componentDidMount(){
		const {id} = this.props.match.params

		const response =  await api.get('/bots/'+id)

		this.setState({ bot: response.data})
	}

	send(event) {
		const {id} = this.props.match.params
	    alert('A name was submitted: ' + this.state.newName);
	    api.put('/bots/'+id,{name:this.state.newName},{header:{'Content-Type':'aplication/json'}}).then(response =>{
	    	console.log(response)
	    })
	    this.setState({redirect: true})
	    
  	}

  	handleChange(event) {
    	this.setState({newName: event.target.value});
  	}

	render(){
		const { bot } = this.state
		const { redirect } = this.state
		
		
		if(redirect){
			return <Routes />
		}else{
			return (
			<div className="bot-info">
				<form onSubmit={this.send}>
					<span> Nome atual: <strong>{bot.name}</strong></span>
					<p> Qual ser√° o novo nome? </p>
					<input type='text' value={bot.newName} onChange={this.handleChange} />
					<input type="submit" value="Salvar" />
				</form>
			</div>
		)
		}
	}
}
