import React, { Component } from 'react';
import api from '../../services/api'
import './styles.css'
import { Link } from 'react-router-dom'

export default class MainChat extends Component {
	


	/*render(){
		
		
		return (
			<div className="bots-list">
					<article>
						<strong>Selecione o que deseja fazer</strong>
						
						<Link to={`/bots`}> Ver todas as mensagens de uma determinada conversa </Link>
						<Link to={`/bots`}> Registrar nova mensagem</Link>
						<Link to={`/bots`}> Retornar uma mensagem pelo seu identificador único</Link>
						
					</article>
			</div>
		)



	}*/
	state = {
		messages: [],
		docInfo: {},
		page: 1,

	}

	//Load that values
	componentDidMount(){
		this.loadBots()
	}

	//Fuction used to load bot informations. note that we call docs in the data because we used
	//pagination in the api.
	loadMessages = async (page = 1) => {
		const response = await api.get(`/bots?page=${page}`)
		console.log(response.data)
		const {docs, ...docInfo } = response.data 
		this.setState({messages: docs, docInfo, page})
	}

	//Function used to go to next page * Same idea! Do nothing when the users are in the last page
	nextPage = () =>{
		const { page, docInfo} = this.state;

		if(page=== docInfo.pages) return

		const pageNumber = page + 1;

		this.loadBots(pageNumber)
	}
	
	//Function used to go to previous page *Do nothing when the users are in the first page
	prevPage = () =>{
		const { page, docInfo} = this.state;

		if(page=== 1) return

		const pageNumber = page -1;

		this.loadBots(pageNumber)
	}



	render(){
		const { messages, page, docInfo } = this.state
		
		return (
			<div className="messages-list">
				{messages.map(bot =>(
					<article key={messages._id}>
						<strong>{messages.name}</strong>
						<p>Criado em {messages.createdAt}</p>
						<Link to={`/messages/${messages._id}`}> Editar </Link>
						<Link to={`/messages/${messages._id}`}> Deletar </Link>
					</article>
				))}

				<div className="actions">
					<button disabled={page === 1} onClick={this.prevPage}>
						Anterior
					</button>
					<button disabled={page === docInfo.pages} onClick={this.nextPage}>
						Próximo
					</button>
				</div>

			</div>
		)
	}
}
