  	}

	render(){
		const { bot } = this.state

		
		return (
			<div className="message-create">
				<form onSubmit={this.send}>
					<strong>Preencha todos os campos</strong>
					<p>ID conversa </p>
						<input className='text-area' type='text' value='' onChange={this.handleChange('input',1)} />
