						<input className='text-area' type='text' value='' onChange={this.handleChange} />
					<p>Mensagem </p>
						<input className='text-area' type='text' value='' onChange={this.handleChange} />
					
					<input className type="submit" value="Salvar" />
				</form>
			</div>
		)
	}
}
